if (!$forum_db->table_exists('torrents')) {
	$schema = array(
		'FIELDS'	=>	array(
			'id'			=> array(
			'datatype'	=> 'SERIAL',
			'allow_null'	=> false
			),
			'info_hash'	=>	array(
				'datatype'	=> 'VARCHAR(40)',
				'allow_null'	=> false,
				'default'	=> '\'\'',
			),
			'announce_url'	=>	array(
				'datatype'	=> 'VARCHAR(255)',
				'allow_null'	=> false,
				'default'	=> '\'\'',
			),
			'date_added'	=>	array(
				'datatype'	=> 'INT(10)',
				'allow_null'	=> false,
				'default'	=> '0',
			),
			'poster_id'	=>	array(
				'datatype'	=> 'INT(10)',
				'allow_null'	=> false,
				'default'	=> '1',
			),
			'poster_ip'	=>	array(
				'datatype'	=> 'VARCHAR(39)',
				'allow_null'	=> false,
				'default'	=>	'\'0.0.0.0\'',
			),
			'name'		=>	array(
				'datatype'	=> 'VARCHAR(255)',
				'allow_null'	=> false,
				'default'	=> '\'\'',
			),
			'num_files'	=>	array(
				'datatype'	=> 'INT(10)',
				'allow_null'	=> false,
				'default'	=> '1',
			),
			'size'		=>	array(
				'datatype'	=> 'BIGINT(20)',
				'allow_null'	=> false,
				'default'	=> '0',
			),
			'last_action'	=>	array(
				'datatype'	=> 'INT(10)',
				'allow_null'	=> false,
				'default'	=> '0',
			),
			'completed'	=>	array(
				'datatype'	=> 'INT(10)',
				'allow_null'	=> false,
				'default'	=> '0',
			),
			'uploaded'	=>	array(
				'datatype'	=> 'BIGINT(20)',
				'allow_null'	=> false,
				'default'	=> '0',
			),
			'downloaded'	=>	array(
				'datatype'	=> 'BIGINT(20)',
				'allow_null'	=> false,
				'default'	=> '0',
			),
		),
		'PRIMARY KEY'	=>	array('id')
	);
	$forum_db->create_table('torrents', $schema);
}

